import React, {useState} from 'react';
export const formBuild = () => {
    const [questions, setQuestions] = useState([]);
    const [title, setTitle] = useState();
    const addQuestion = (type) => {
        const newQuestion = {
            id: Date.now(),
            type: type,
            label: '',
            options: type === 'multiple_choice' ? ['Option 1'] : [],
        };
        setQuestions([...questions, newQuestion])
    };
    const updateQuestion = (id, updatedQuestion) => {
        setQuestions(questions.map(q => (q.id == id ? updatedQuestion : q)));
    };
    const deleteQuestion = (id) => {
        setQuestions(questions.filter(q => (q.id !== id)));
    };
    return(
        <div>
            <h1>Create Survey Form for Patients</h1>
        <input type="text"
        placeholder='Survey Title'
        value={title}
        onChange = {(e) => setTitle(e.target.value)}
        />
        <button onClick = {() => addQuestion('multiple_choice')}>Add Multiple Choice Option</button>
        <button onClick = {() => addQuestion('short_answer')}>Add Short Answer Option</button>
        <button onClick = {() => addQuestion('rating_scale')}>Add Rating Scale Option</button>
        <ul>
            {questions.map((q) => {
                <li key={q.id}>
                    <input type="text"
                    placeholder="Options"
                    value={q.label}
                    onChange={(e) => updateQuestion(q.id, {...q, label: e.target.value})}
                    />
                    {q.type == 'multiple_choice' && (
                        <div>
                            {q.options.map((opt, index) => (
                                <input
                                    key={index}
                                    type="text"
                                    value={opt}
                                    onChange={(e) => {
                                        const newOptions = [...q.options];
                                        newOptions[index] = e.target.value;
                                        updateQuestion(q.id, { ...q, options: newOptions });
                                    }}
                                />
                            ))}
                            <button
                                onClick={() =>
                                    updateQuestion(q.id, {
                                        ...q,
                                        options: [...q.options, `Option ${q.options.length + 1}`],
                                    })
                                }
                            >
                                Add Option
                            </button>
                        </div>
                    )}
                    <button onClick={() => deleteQuestion(q.id)}>Delete</button>
                </li>
            })}
        </ul>
        <button onClick={() => {
                    console.log({ title: surveyTitle, questions });
                    alert('Survey Saved!');
                }}>Save Survey</button>     
        </div>
    )
}
